export default function Theme () {

    const setThemeDark  = () => {
        document.querySelector("body").setAttribute('data-theme' , 'dark')
    }
    
    const setThemeLight  = () => {
        document.querySelector("body").setAttribute('data-theme' , 'light')
    }

    const ToggleTheme = e => {
        if(e.target.checked) {}
    }

    return (
        <>
        <div className='theme'>
            <input  className='theme_input' type='checkbox' id='theme-toggle' />
        </div>
        </>
    )
};
