import React, { useState , useEffect } from "react";
import Navbar from "../components/navbar"; 

import "../stylesheet/main.css"


export default function Main() {

    const  [counter,setCounter] = useState(0); 
    const [value , setValue] = useState(0);
    const [add ,  addValue] = useState(0)



    function buttonadd () {

    }

    return (
        <>
        <Navbar /> 
        <div>
            <h1>{counter}</h1>
            <button onClick={setCounter(counter + 1)}>+</button>
            <button onClick={setCounter(counter - 1)}>-</button>
            <button onClick={setCounter(counter-counter)}>restart</button>
         
            <input type="numbers" onChange={(e) => {setValue(e.target.value)}} placeholder="set number" />
            <button type="button" onClick={setCounter(counter + value)}>set</button>
       
        
            <input type="text" onChange={(e) => {addValue(e.target.value)}} placeholder="add number"/>
            <button type="button" onClick={buttonadd()}>add</button>
           
            {/* <button>add</button> */}
        </div>
        </>
    );
}
