import { MdDarkMode , MdLightMode } from "react-icons/md";


import '../stylesheet/compsStyle/theme.css'; 
import { useEffect, useState } from "react";



export default function Theme () {

    const [theme,setTheme] = useState('light'); 

var BodySelector = document.querySelector("body"); 

useEffect(() => {
    const setThemeDark  = () => {
        BodySelector.setAttribute('data-theme' , 'dark'); 
        localStorage.setItem("selectedTheme" , "dark");
        // setTheme('dark'); 
    }
    
    const setThemeLight  = () => {
        BodySelector.setAttribute('data-theme' , 'light');
        localStorage.setItem("selectedTheme" , "light"); 
        setTheme('light'); 

    }

    const selectedTheme = localStorage.getItem('selectedTheme'); 
    if(selectedTheme === "dark") {
        setThemeDark(); 
    }

    const toggleTheme = e => {
        if(BodySelector.getAttribute('data-theme') === "dark") setThemeLight();
        else if(BodySelector.getAttribute('data-theme') === "light") setThemeDark();
    }
} , [])
   

    // function ThemeNow() {
    //     if(theme === "dark") return <MdDarkMode/>
    //     else if(theme === "light") return <MdLightMode/>
    // }

    // var thenow = ThemeNow();
useEffect(() => {} , [])
    return (
        <>
        <div className='theme'>
          <button defaultChecked={selectedTheme === "dark"} onClick={toggleTheme}>
                {() => {
                    if(BodySelector.getAttribute('data-theme') === "dark" || selectedTheme === "dark") return <MdDarkMode/>
                    else if(BodySelector.getAttribute('data-theme') === "light" || selectedTheme === "light") return <MdLightMode/>  
                }}
          </button>
        </div>
        </>
    )
};
