import Hamburger from 'hamburger-react';
import { useEffect, useState } from 'react';
import {Link , NavLink } from 'react-router-dom';
import Theme from './theme';
import '../stylesheet/compsStyle/navbar.css';
export default function Navbar () {

    const [isOpen, setOpen] = useState(false)
    const [selectedTheme, setSelectedTheme] = useState(localStorage.getItem('selectedTheme') || 'light');

    const [BurgerC, setBurgerC] = useState(<MdLightMode color="black" />);
    const BodySelector = document.querySelector("body"); 

    useEffect(() => {
        const selectedTheme = localStorage.getItem('selectedTheme');
        if(selectedTheme === "dark") {
            setBurgerC(<MdDarkMode size={'23px'} color="white" />);
            BodySelector.setAttribute('data-theme' , 'dark');
        } else {
            setBurgerC(<Hamburger size={'23px'} color="black" />); 
            BodySelector.setAttribute('data-theme' , 'light');
        }
    }, [BodySelector]);
    return (
        <>
        <header className='navbar'>
            <div className='navbar_logo'>UniverTools</div>
            <Hamburger/>
            <nav className='navbar_li'>
                <ul>
                    <li>
                       <NavLink exact to="/" className={({isActive}) => isActive ? "active-navbar": "" }>جميع الخدمات</NavLink>
                    </li>
                    <li>
                       <NavLink exact to="/balanced" className={({isActive}) => isActive ? "active-navbar": "" }>حساب الموزونة</NavLink>
                    </li>
                    <li>
                       <NavLink exact  to="/age" className={({isActive}) => isActive ? "active-navbar": "" }>حساب العمر</NavLink>
                    </li>
                    <li>
                  <a>
                  <Theme/>
                  </a>
                    </li>
                </ul>
            </nav>
        </header>
        </>
    )
};
