import '../stylesheet/compsStyle/theme.css'; 



export default function Theme () {

    const setThemeDark  = () => {
        document.querySelector("body").setAttribute('data-theme' , 'dark'); 
        localStorage.setItem("selectedTheme" , "")
    }
    
    const setThemeLight  = () => {
        document.querySelector("body").setAttribute('data-theme' , 'light')
    }

    const toggleTheme = e => {
        if(e.target.checked) setThemeDark();
        else setThemeLight(); 
    }

    return (
        <>
        <div className='theme'>
            <input onChange={toggleTheme}  className='theme_input' type='checkbox' id='theme-toggle' />
        </div>
        </>
    )
};
