import React, { useEffect, useState } from "react";
import Navbar from "../components/navbar"; 

import "../stylesheet/main.css"


export default function Main() {

    const  [counter,setCounter] = useState(0); 
    const [value , setValue] = useState(null);
    const [add ,  addValue] = useState(null)

    function increase () {
        setCounter(counter + 1);
    }
    function decrease () {
        setCounter(counter - 1);
    }
    function restart () {
        setCounter(counter - counter); 
    }





    return (
        <>
        <Navbar /> 
        <div>
            <h1>{counter}</h1>
            <button onClick={() => {setCounter(counter + 1)}}>+</button>
            <button onClick={() => {setCounter(counter - 1)}}>-</button>
            <button onClick={() => { setCounter(counter - counter)}}>restart</button>
         
            <input value={value} type="numbers" onChange={(e) => {setValue(e.target.value)}} placeholder="set number" />
            <button type="button" onClick={() => {setCounter(value)}}>set</button>
            <input value={add} type="numbers" onChange={(e) => {addValue(e.target.value)}} placeholder="add number" />
            <button type="button" onClick={() => {setCounter(counter + parent(value))}}>add</button>
          
        </div>
        </>
    );
}
