import React, { useState } from "react";
import Navbar from "../components/navbar"; 

import "../stylesheet/main.css"


export default function Main() {

    const  [counter,setCounter] = useState(0); 
    const [value , setValue] = useState(0);
    const [add ,  addValue] = useState(0)
    function increase () {
        setCounter(counter + 1);
    }
    function decrease () {
        setCounter(counter - 1);
    }
    function restart () {
        setCounter(counter - counter); 
    }


    function addNumber(num) {
        setCounter(counter + num); 
    }



    return (
        <>
        <Navbar /> 
        <div>
            <h1>{counter}</h1>
            <button onClick={increase}>+</button>
            <button onClick={decrease}>-</button>
            <button onClick={restart}>restart</button>
         
            <input type="numbers" onChange={(e) => {setValue(e.target.value)}} placeholder="set number" />
            <button type="button" onClick={addValue(value)}>set</button>
       
        
            <input type="text" onChange={(e) => {addValue(e.target.value)}} placeholder="add number"/>
            <button type="button" onClick={setCounter(value)}>add</button>
           
            {/* <button>add</button> */}
        </div>
        </>
    );
}
