import '../stylesheet/compsStyle/services.css'; 


export default function Services () {

    const setThemeDark  = () => {
        document.querySelector("body").setAttribute('data-theme' , 'dark')
    }
    
    const setThemeLight  = () => {
        document.querySelector("body").setAttribute('data-theme' , 'dark')
    }

    return (
        <>
        <div className='theme'>
            <input  className='theme_input' type='checkbox' id='theme-toggle' />
        </div>
        </>
    )
};
